
import textwrap

def print_clean():
    with open(r'C:\Users\anpaz\Repos\quantum-ai\rq1\data\gemini-3-pro-preview\agent_files\circuit_133.stim', 'r') as f:
        content = f.read()
    
    # It seems the file might have long lines. Stim handles newlines between instructions fine.
    # But newlines inside an instruction (like `CX 0 1\n 2 3`) might be risky if it splits a number.
    # However, if the file was generated by stim, it should be valid.
    # I just want to print it such that I can copy it.
    # If I print it to stdout, the agent tool might wrap it.
    # But wait, `final_circuit` takes a string. I just need to provide that string.
    # I can rely on the fact that `view` shows me the content.
    
    # Reformat to avoid long lines
    for line in content.splitlines():
        parts = line.split()
        current_line = []
        current_len = 0
        for part in parts:
            if current_len + len(part) + 1 > 80:
                print(" ".join(current_line))
                current_line = [part]
                current_len = len(part)
            else:
                current_line.append(part)
                current_len += len(part) + 1
        if current_line:
            print(" ".join(current_line))

if __name__ == '__main__':
    print_clean()
